package uk.ac.ebi.fg.annotare2.db.dao.impl;import com.google.inject.Inject;import uk.ac.ebi.fg.annotare2.db.dao.SubmissionStatusHistoryDao;import uk.ac.ebi.fg.annotare2.db.model.Submission;import uk.ac.ebi.fg.annotare2.db.model.SubmissionStatusHistory;import uk.ac.ebi.fg.annotare2.db.util.HibernateSessionFactory;import java.time.LocalDateTime;public class SubmissionStatusHistoryDaoImpl        extends AbstractDaoImpl<SubmissionStatusHistory> implements SubmissionStatusHistoryDao {    @Inject    public SubmissionStatusHistoryDaoImpl(HibernateSessionFactory sessionFactory) {        super(sessionFactory);    }    @Override    public void saveStatusHistory(Submission submission) {        SubmissionStatusHistory history = new SubmissionStatusHistory();        history.setSubmission(submission);        history.setStatus(submission.getStatus());        history.setChangedAt(LocalDateTime.now());        save(history);    }}