package uk.ac.ebi.fg.annotare2.db.model;import uk.ac.ebi.fg.annotare2.db.model.enums.SubmissionStatus;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.EnumType;import javax.persistence.Enumerated;import javax.persistence.FetchType;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import java.time.LocalDateTime;@Entity@Table(name = "submission_status_history")public class SubmissionStatusHistory {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "submission_id", nullable = false)    private Submission submission;    @Enumerated(EnumType.STRING)    @Column(name = "status", nullable = false)    private SubmissionStatus status;    @Column(name = "changed_at", nullable = false, columnDefinition = "TIMESTAMP DEFAULT CURRENT_TIMESTAMP")    private LocalDateTime changedAt;    @Column(name = "changed_by")    private String changedBy;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Submission getSubmission() {        return submission;    }    public void setSubmission(Submission submission) {        this.submission = submission;    }    public SubmissionStatus getStatus() {        return status;    }    public void setStatus(SubmissionStatus status) {        this.status = status;    }    public LocalDateTime getChangedAt() {        return changedAt;    }    public void setChangedAt(LocalDateTime changedAt) {        this.changedAt = changedAt;    }    public String getChangedBy() {        return changedBy;    }    public void setChangedBy(String changedBy) {        this.changedBy = changedBy;    }}