changeSet.sql:
ALTER TABLE submissions ADD version ENUM('VERSION_1_0','VERSION_1_1') AFTER id;
UPDATE submissions SET version = 'VERSION_1_0';
ALTER TABLE submissions MODIFY version ENUM('VERSION_1_0','VERSION_1_1') NOT NULL;

ALTER TABLE acl_entries MODIFY permission ENUM('CREATE', 'UPDATE', 'VIEW', 'ASSIGN') NOT NULL;
SELECT @aclId := id FROM acl WHERE aclType = 'SUBMISSION';
INSERT INTO acl_entries (acl, role, permission) VALUES (@aclId, 'CURATOR', 'ASSIGN');

ALTER TABLE submission_feedback DROP FOREIGN KEY submission_feedback_ibfk_1;
ALTER TABLE submission_feedback CHANGE relatesTo submissionId BIGINT NOT NULL;
ALTER TABLE submission_feedback ADD CONSTRAINT FOREIGN KEY (submissionId) REFERENCES submissions (id)
ON DELETE CASCADE;

create table if not exists submission_status_history ( id bigint auto_increment primary key, submission_id bigint not null, status varchar(255) not null, changed_at timestamp default CURRENT_TIMESTAMP not null, changed_by varchar(255) null, constraint submission_id_fk foreign key (submission_id) references submissions (id) on delete cascade ) charset = utf8mb4;
create index submission_status_history_idx_created_at on submission_status_history (changed_at);
create index submission_status_history_idx_submission_id on submission_status_history (submission_id);

create table submissions_exceptions ( id BIGINT auto_increment, submission_id BIGINT not null, created TIMESTAMP not null, exception_message TEXT not null, exception_stacktrace TEXT not null, is_fixed boolean not null, constraint submissions_exceptions_pk primary key (id), constraint submissions_exceptions_fk foreign key (submission_id) references submissions (id) on delete cascade );
create index submissions_exceptions_idx_created on submissions_exceptions (created desc);
create index submissions_exceptions_idx_submission_id on submissions_exceptions (submission_id);
