package uk.ac.ebi.fg.annotare2.web.gwt.common.shared;import com.google.gwt.user.client.rpc.IsSerializable;public class CookiePopupDeatils implements IsSerializable {    private String name;    private Long expiryDate;    private Long stopNoticeDate;    private String title;    private String html;    public CookiePopupDeatils() {    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Long getExpiryDate() {        return expiryDate;    }    public void setExpiryDate(Long expiryDate) {        this.expiryDate = expiryDate;    }    public Long getStopNoticeDate() {        return stopNoticeDate;    }    public void setStopNoticeDate(Long stopNoticeDate) {        this.stopNoticeDate = stopNoticeDate;    }    public String getTitle() {        return title;    }    public void setTitle(String title) {        this.title = title;    }    public String getHtml() {        return html;    }    public void appendHTML(String html) {        if(null == this.html){            this.html = html;        } else{            this.html += html;        }    }}